import React, { useState } from 'react';
import './App.css';
import strawberryImage from './Strawberry transparent.png';

const App = ({ onConfirmPlanting }) => {
  const [modalOpen, setModalOpen] = useState(false);

  const handleImageClick = () => {
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
  };

  const handleConfirmPlantingClick = () => {
    setModalOpen(false);
    onConfirmPlanting(); // Call the function passed from the parent component (Root)
  };

  return (
    <div>
      <header className="logo">
        Sprout View ðŸª´    
      </header>
      <main>
        <div className="searchBarContainer">
          <input
            className="searchBar"
            type="text"
            placeholder="Search plants..."
            onChange={() => {}}
          />
        </div>
        <div className="imageContainer">
          <img 
            className="plantImage" 
            src={strawberryImage} 
            alt="Strawberry"
            onClick={handleImageClick}
          />
        </div>
        {modalOpen && (
          <div className="modal">
            <div className="modal-content">
              <span className="close" onClick={handleCloseModal}>&times;</span>
              <h2>Are you sure you want to plant a Strawberry?</h2>
              <button onClick={handleConfirmPlantingClick}>Yes</button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default App;
