// App.js
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import strawberryImage from './Strawberry transparent.png';

function App({ onConfirmPlanting }) {
  const [modalOpen, setModalOpen] = useState(false);
  const history = useHistory();

  const handleImageClick = () => {
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
  };

  const handleConfirmPlanting = () => {
    setModalOpen(false);
    onConfirmPlanting();
    history.push('/options'); // Navigate to the Options route
  };

  return (
    <div>
      <header className="logo">
        Sprout View ðŸª´    
      </header>
      <main>
        <div className="searchBarContainer">
          <input
            className="searchBar"
            type="text"
            placeholder="Search plants..."
          />
        </div>
        <div className="imageContainer">
          <img 
            className="plantImage" 
            src={strawberryImage} 
            alt="Strawberry"
            onClick={handleImageClick}
          />
        </div>
        {modalOpen && (
          <div className="modal">
            <div className="modal-content">
              <span className="close" onClick={handleCloseModal}>&times;</span>
              <h2>Are you sure you want to plant a Strawberry?</h2>
              <button onClick={handleConfirmPlanting}>Yes</button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
